<?php $hasil=$result->query_x1("SELECT angsurke,bulan,tanggal,SUM(if(kdtran=111,pokok,0))-SUM(if(kdtran=777,pokok,0)) AS pokok,SUM(if(kdtran=111,bunga,0))-SUM(if(kdtran=777,bunga,0)) AS bunga,SUM(if(kdtran=111,adm,0))-SUM(if(kdtran=777,adm,0)) AS adm FROM $tabel_payment WHERE norek='$no_paymet' AND angsurke<='$angsurke' GROUP BY angsurke,bulan ORDER BY angsurke,bulan");$jpokok=0;$jbunga=0;$jadm=0;$jkk=0;$kk=0;while($r=$result->row($hasil)){$jkk=$r['pokok']+$r['bunga']+$r['adm'];$jbunga=$jbunga+$r['bunga'];$jadm=$jadm+$r['adm'];if($jkk>0){$kk++;}} ?>