<?php $id=$result->c_d($_GET['id']);$hasil=$result->query_lap("SELECT a.norek,a.nama,a.tgl_lahir,a.tmp_lahir,a.tgl_mati,a.tmp_mati,a.sebab_mati,a.ket_mati,a.tgl_klaim,a.jenis_klaim,a.tgl_jatuh_tempo,a.plafond,a.saldo,a.jumlah_klaim,a.user_klaim,a.nama_ahli_waris,c.nmproduk,b.produk,b.kdkop,b.umur,b.jangka,b.tgtran,b.kdpremi,b.nopremi FROM $tabel_klaim a JOIN $tabel_kredit b ON a.norek=b.norek JOIN debit1 c ON b.produk=c.kdproduk WHERE a.kode_hapus=0 AND a.status_klaim=1 AND a.id_kredit='$id' LIMIT 1");$row=$result->row($hasil); ?>