<?php include 'h_tetap.php';$id=$result->c_d($_GET['id']);$hasil=$result->res("SELECT id,nonas,norek,nama,jumlah,kdtran,keterangan FROM $tabelTransaksi WHERE id='$id'");$row=$result->row($hasil);$data['id'] = $row['id'];$data['text1']=substr($row['norek'],0,4);$data['text2']=substr($row['norek'],4,6);$data['text3']=substr($row['norek'],-3);$data['nama']=$row['nama'];$data['kdtran']=$row['kdtran'];$data['jumlah']=$row['jumlah'];$data['kete']=$row['keterangan'];$data['pesan'] = 'Sukses';echo json_encode($data); ?>