<?php $result->query_x1("CREATE TEMPORARY TABLE $userid SELECT jtransaksi,oper,tanggal,notransaksi,SUM(IF(kdkredit=10,jumlah,0)) AS pokok,SUM(IF(kdkredit=20,jumlah,0)) AS bunga,SUM(IF(kdkredit=30,jumlah,0)) AS adm,SUM(IF(kdkredit=40,jumlah,0)) AS denda,SUM(IF(kdkredit=50,jumlah,0)) AS finalti,IF(SUBSTR(kdtran,1,1)<=2,'K','M') AS mutasi FROM $tabelTransaksi WHERE (kdkredit=10 OR kdkredit=20 OR kdkredit=30 OR kdkredit=40 OR kdkredit=50) AND norek='$norek' GROUP BY norek,notransaksi ORDER BY norek,notransaksi");$hasil=$result->query_x1("SELECT jtransaksi,pokok,bunga,adm,denda,finalti,pokok+bunga+adm+denda+finalti AS jumlah,mutasi,oper,tanggal,notransaksi FROM $userid ORDER BY tanggal,notransaksi");if($result->num($hasil)>0){echo "<p><table width='100%' class='table'>";include 'tabel_hasil.php';echo "</table>";} ?>