<?php $bln=$result->c_d($_GET['bln']);$thn=$result->c_d($_GET['thn']);$kkbayar =$result->c_d($_GET['kkbayar']);$tabel=$tabel_tagihan.$bln.substr($thn,2,2).'b';$branch=$result->c_d($_GET['branch']);$tabels = $tabel_tagihan.$bln.substr($thn,2,2).'s';if($branch=='0111'){if($kkbayar=='9'){$hasil=$result->query_lap("SELECT a.norek,a.nama,a.angsurke,a.jumlah,a.kdtran,b.nocitra,b.nopen,b.kdpin,b.jangka,b.kkbayar,b.nmbayar,b.no_aso_bank,b.no_cif_bank,b.tgtran,b.umur,b.tgl_jatuh_tempo,c.jumlah as jumlah1 FROM $tabel a JOIN $tabel_kredit b ON a.norek=b.norek LEFT JOIN $tabels c ON a.norek=c.norek WHERE b.produk!='KMB' AND b.produk!='KMH' AND b.produk!='KMM' ORDER BY b.kkbayar,a.norek");}else{$hasil=$result->query_lap("SELECT a.norek,a.nama,a.angsurke,a.jumlah,a.kdtran,b.nocitra,b.nopen,b.kdpin,b.jangka,b.kkbayar,b.nmbayar,b.no_aso_bank,b.no_cif_bank,b.tgtran,b.umur,b.tgl_jatuh_tempo,c.jumlah as jumlah1 FROM $tabel a JOIN $tabel_kredit b ON a.norek=b.norek LEFT JOIN $tabels c ON a.norek=c.norek WHERE b.produk!='KMB' AND b.produk!='KMH' AND b.produk!='KMM' AND b.kkbayar='$kkbayar' ORDER BY b.kkbayar,a.norek");}}else{if($kkbayar=='9'){$hasil=$result->query_lap("SELECT a.norek,a.nama,a.angsurke,a.jumlah,a.kdtran,b.nocitra,b.nopen,b.kdpin,b.jangka,b.kkbayar,b.nmbayar,b.no_aso_bank,b.no_cif_bank,b.tgtran,b.umur,b.tgl_jatuh_tempo,c.jumlah as jumlah1 FROM $tabel a JOIN $tabel_kredit b ON a.norek=b.norek LEFT JOIN $tabels c ON a.norek=c.norek WHERE b.produk!='KMB' AND b.produk!='KMH' AND b.produk!='KMM' AND b.branch='$branch' ORDER BY b.kkbayar,a.norek");}else{$hasil=$result->query_lap("SELECT a.norek,a.nama,a.angsurke,a.jumlah,a.kdtran,b.nocitra,b.nopen,b.kdpin,b.jangka,b.kkbayar,b.nmbayar,b.no_aso_bank,b.no_cif_bank,b.tgtran,b.umur,b.tgl_jatuh_tempo,c.jumlah as jumlah1 FROM $tabel a JOIN $tabel_kredit b ON a.norek=b.norek LEFT JOIN $tabels c ON a.norek=c.norek WHERE b.produk!='KMB' AND b.produk!='KMH' AND b.produk!='KMM' AND b.kkbayar='$kkbayar' AND b.branch='$branch' ORDER BY b.kkbayar,a.norek");}} ?>