<?php $bln=$result->c_d($_GET['bln']);$thn=$result->c_d($_GET['thn']);$kkbayar =$result->c_d($_GET['kkbayar']);$tglakhir=tglAkhirBulan($thn,intval($bln));$tabeln=$userid.'s';$m =$thn.'-'.$bln.'-'.$tglakhir;$branch=$result->c_d($_GET['branch']);if($branch=='0111'){$result->query_x1("CREATE TEMPORARY TABLE $tabeln SELECT a.norek,a.nama,a.angsurke,a.kdtran,(SUM(if(a.kdtran=111,a.pokok,0))-SUM(if(a.kdtran=777,a.pokok,0))+SUM(if(a.kdtran=111,a.bunga,0))-SUM(if(a.kdtran=777,a.bunga,0))+SUM(if(a.kdtran=111,a.adm,0))-SUM(if(a.kdtran=777,a.adm,0))) AS jumlah FROM $tabel_payment a JOIN $tabel_kredit b ON a.norek=b.norek WHERE b.tgl_jatuh_tempo<='$m' AND a.tanggal<='$m' AND b.saldo>0 AND b.ketnas!=1 GROUP BY norek ORDER BY norek");}else{$result->query_x1("CREATE TEMPORARY TABLE $tabeln SELECT a.norek,a.nama,a.angsurke,a.kdtran,(SUM(if(a.kdtran=111,a.pokok,0))-SUM(if(a.kdtran=777,a.pokok,0))+SUM(if(a.kdtran=111,a.bunga,0))-SUM(if(a.kdtran=777,a.bunga,0))+SUM(if(a.kdtran=111,a.adm,0))-SUM(if(a.kdtran=777,a.adm,0))) AS jumlah FROM $tabel_payment a JOIN $tabel_kredit b ON a.norek=b.norek WHERE b.tgl_jatuh_tempo<='$m' AND a.tanggal<='$m' AND b.saldo>0 AND b.ketnas!=1 AND b.branch='$branch' GROUP BY norek ORDER BY norek");}if($kkbayar=='9'){$hasil=$result->query_lap("SELECT a.norek,a.nama,a.angsurke,a.jumlah,a.kdtran,b.tgl_jatuh_tempo,b.nocitra,b.nopen,b.kdpin,b.jangka,b.kkbayar,b.nmbayar,b.no_aso_bank,b.no_cif_bank,b.tgtran,b.umur,(b.pokok+b.bunga+b.administrasi) AS tagihan,b.ketkolek FROM $tabeln a JOIN $tabel_kredit b ON a.norek=b.norek WHERE a.jumlah>0 AND b.produk!='KMH' AND b.produk!='KMM' AND b.produk!='KMB' ORDER BY b.kkbayar,a.norek");}else{$hasil=$result->query_lap("SELECT a.norek,a.nama,a.angsurke,a.jumlah,a.kdtran,b.tgl_jatuh_tempo,b.nocitra,b.nopen,b.kdpin,b.jangka,b.kkbayar,b.nmbayar,b.no_aso_bank,b.no_cif_bank,b.tgtran,b.umur,(b.pokok+b.bunga+b.administrasi) AS tagihan,b.ketkolek FROM $tabeln a JOIN $tabel_kredit b ON a.norek=b.norek WHERE a.jumlah>0 AND b.produk!='KMH' AND b.produk!='KMM' AND b.produk!='KMB' AND b.kkbayar='$kkbayar' ORDER BY b.kkbayar,a.norek");} ?>